import "./cart.css";

import { useContext } from "react";
import StoreContext from "../store/storeContext";


const Cart = () => {
  const cart = useContext(StoreContext).cart;
  
  const getNumberItems = () => {
  let sum = 0;
 
  for(let i=0; i < cart.length; i++) {
    let prod = cart[i];
    sum += prod.quantity;
  }
  return sum;
};

  return (
    <div className="cart">
      <h1>Ready to pay?</h1>
      <h5>we have {getNumberItems()} item ready for you</h5>

      <ul>
        {cart.map(prod => <productInCart key={prod._id} data={prod}></productInCart>)}
      </ul>
    </div>
  );
};



export default Cart;
