import StoreContext from "./storeContext";
import { useState } from "react";

const GlobalState = (props) => {
    const [cart, setCart]= useState([]);

    return (
        <StoreContext.Provider value={{
            cart: []
        }}>
            {props.children}
        </StoreContext.Provider>
    );
};

export default GlobalState;
